Addon.JavascriptInHead:
function saveSortable(listId){
	var e,c,s;
	s=listId;
	e=document.getElementById(listId);
	for (i=0;i<e.childNodes.length;i++){
		c=e.childNodes[i];
		if(c.id) {s+=','+c.id }
	}
	cj.ajax.addon('savePageManagerChildListSort','sortlist='+s)
}
jQuery(document).ready(function() {
	jQuery(".ccEditWrapper .ccChildList").sortable({
			stop: function(event, ui) {
				saveSortable(jQuery(this).attr('id')); 
			}
	});
})

Addon.Styles:
.ccEditWrapper .ccChildList li {
	min-height:30px;
	vertical-align:middle;
	border:1px dashed #262;
	padding-right:40px !important;background:white url(/childPageSort/arrowUpDown.png) no-repeat right;
}

