function CheckInputs(TargetID,SetValue){
  var e,ptr;
  e=document.getElementsByTagName('input');
  for (ptr=0;ptr<e.length;ptr++){
    if(e[ptr].id==TargetID){e[ptr].checked=SetValue}
  }
}
