//
// ----- hide select menus
//
function hideselect(hiddenIn) {						
	var i, j, index, objs, wmode, paramName
	//
	if (hiddenIn) {
		menuClicks++;
	}
	//
	if (!hiddenIn && menuClicks == 0 && !selectsHidden) {
		return;
	}
	// Hide MS Selects
	if (browser.isIE) {
		//alert('ie');
		objs = document.getElementsByTagName("SELECT");
		for (i = 0; i < objs.length; i++) {
			if (selectsHidden && menuClicks == 0) {
				objs[i].style.visibility = "";
			} else {
				objs[i].style.visibility = "hidden";
			}
		}
		// Hide Objects if no <Param name=wmode> for ie
		objs = document.getElementsByTagName("OBJECT");
		for (i = 0; i < objs.length; i++) {
			if (selectsHidden && menuClicks == 0) {
				objs[i].style.visibility = "";
			} else {
				//var wmodeSet=false;
				var p=objs[i].getElementsByTagName("param");
				for (pi = 0; pi < p.length; pi++){
					paramName=p[pi].name.toLowerCase();
					if (paramName=='wmode') {
						wmode=p[pi].value;
						if(wmode) {
							wmode = wmode.toLowerCase();
							if (wmode=='opaque') {
								// it hides itself
							} else if (wmode=='transparent') {
								// it hides itself
							} else if (wmode=='window') {
								// it hides itself
								objs[i].style.visibility = "hidden";
							} else {
								objs[i].style.visibility = "hidden";
							}
						}
					}
				}
			}
		}
	} else {
		// Hide MS Embeds for Mozilla
		objs = document.getElementsByTagName("EMBED");
		for (i = 0; i < objs.length; i++) {
			if (selectsHidden && menuClicks == 0) {
				objs[i].style.visibility = "";
			} else {
				wmode=objs[i].getAttribute("wmode")
				if(wmode) {
					wmode = wmode.toLowerCase();
				}
				if (wmode=='opaque') {
					// it hides itself
				} else if (wmode=='transparent') {
					// it hides itself
				} else if (wmode=='window') {
					// it hides itself
				} else {
					objs[i].style.visibility = "hidden";
				}
			}
		}
	}
	//
	if (selectsHidden && menuClicks == 0) {
		selectsHidden = false;
	}
	//
	else if (selectsHidden && menuClicks != 0 && clickButton != 2) {
		menuClicks--;
	}
	//
	else {
		selectsHidden = true;
	}
}
//
// ----- Browser Detect
//
function BrowserType() {
	var ua, s, i;
	this.isIE    = false;  // Internet Explorer
	this.isNS    = false;  // Netscape
	//this.version = null;
	this.isMac = false;
	ua = navigator.userAgent;
	s = "MSIE";
	this.version = 0.0;
	if (navigator.appVersion.indexOf("Mac")!=-1) {
		this.isMac = true;
		}
	if ((i = ua.indexOf(s)) >= 0) {
		this.isIE = true;
		this.version = parseFloat(ua.substr(i + s.length));
		return;
		}
	s = "Netscape6/";
	if ((i = ua.indexOf(s)) >= 0) {
		this.isNS = true;
		this.version = parseFloat(ua.substr(i + s.length));
		return;
		}
	s = "Gecko";
	if ((i = ua.indexOf(s)) >= 0) {
		this.isNS = true;
		this.version = 6.1;
		return;
		}
	}
//
// ----- Code for handling the menu bar and active button.
//
function PageClick(event) {

	var el;

	if (activeButton == null) return;
	if (browser.isIE)
		el = window.event.srcElement;
	else
		el = (event.target.tagName ? event.target : event.target.parentNode);

	if (el == activeButton)
		return;
	if (getContainerWith(el, "DIV", "kmaMenu") == null) {
		resetButton(activeButton);
		activeButton = null;
	}
}
//
// ----- Clear the menu
//
function ClearMenu() {
	if (activeButton == null) return;
	resetButton(activeButton);
	activeButton = null;
	}
//
// -----
//
function ccFlyoutHoverMode(val) {
	flag = val;
	if (val == 0) {
		setTimeout(timerEvent, 1000);
	}
	if (val == 1) {
		updated = 1;
	}
	return;
}
//
// -----
//
function timerEvent() {

	if (flag == 0) {
		resetFlag = 1;
		resetButton(activeButton);
		menuClicks = 0;
		hideselect(0);
		updated = 1;
	}

}
