function SubmitToolsPanel() {
    var SelectedObject;
    var TextRange;
    SelectedObject = document.selection;
    TextRange = SelectedObject.createRange();
    if (TextRange.text) {
        if (TextRange.text.length != 0) {
            document.all['ToolsPanelSelection'].value=TextRange.htmlText;
        }
    }
    return true;
}
