//
//-------------------------------------------------------------------------
// Add an onLoad event
// source webreference.com, SimonWilson.net
//-------------------------------------------------------------------------
//
function cjAddLoadEvent(func) { 
	var oldonload = window.onload; 
	if (typeof window.onload != 'function') { 
		window.onload = func; 
	} else { 
		window.onload = function() { 
			if (oldonload) { 
				oldonload(); 
			} 
			func(); 
		} 
	} 
}
