if (browser.isIE) {
	document.onmousedown = PageClick;
	if (document.body) {
		document.body.onclick = BodyOnClick;
		document.body.oncontextmenu = BodyOnClick;
	}
	else {
		window.onclick = BodyOnClick;
		window.oncontextmenu = BodyOnClick;
	}
	updateClipboardLinksInit();
}
else if(browser.version > 5) {
	document.addEventListener("mousedown", PageClick, true);
	document.addEventListener("mouseclick", BodyOnClick, true);
}
/**
 * Emulate FormData for some browsers
 * MIT License
 * (c) 2010 Franois de Metz
 * from https://github.com/francois2metz/html5-formdata
 */
(function(w) {
    if (w.FormData)
        return;
    function FormData() {
        this.fake = true;
        this.boundary = "--------FormData" + Math.random();
        this._fields = [];
    }
    FormData.prototype.append = function(key, value) {
        this._fields.push([key, value]);
    }
    FormData.prototype.toString = function() {
        var boundary = this.boundary;
        var body = "";
        this._fields.forEach(function(field) {
            body += "--" + boundary + "\r\n";
            // file upload
            if (field[1].name) {
                var file = field[1];
                body += "Content-Disposition: form-data; name=\""+ field[0] +"\"; filename=\""+ file.name +"\"\r\n";
                body += "Content-Type: "+ file.type +"\r\n\r\n";
                body += file.getAsBinary() + "\r\n";
            } else {
                body += "Content-Disposition: form-data; name=\""+ field[0] +"\";\r\n\r\n";
                body += field[1] + "\r\n";
            }
        });
        body += "--" + boundary +"--";
        return body;
    }
    w.FormData = FormData;
})(window);

