Addon.JavascriptInHead:
//
// -- Child Page Sorting
jQuery(function () {
    jQuery(".ccChildList").sortable({
        stop: function (event, ui) {
            var e, c, s;
            s = jQuery(this).attr('id');
            e = document.getElementById(listId);
            for (i = 0; i < e.childNodes.length; i++) {
                c = e.childNodes[i];
                if (c.id) { s += ',' + c.id }
            }
            cj.ajax.addon('ChildListResort', 'sortlist=' + s);
        }
    });
});

Addon.Styles:
.ccChildList li {
    min-height:30px;
    vertical-align:middle;
    border:1px dashed #262;
    padding-right:40px !important;
    background:white url(/childPageSort/arrowUpDown.png) no-repeat right;
}
